<?php/* Theme - Specific Conditionals */function has_sidebar_left() {	return 		is_page_template('template-sidebar-left.php')	;}function has_sidebar_right() {	return 		is_blog() ||		is_page_template('template-sidebar-right.php')	;}/* Excerpt */function eng_excerpt_length( $length ) {	if ( is_front_page() ) :		return 10;			else :		return 20;			endif;}add_filter( 'excerpt_length', 'eng_excerpt_length', 999 );function eng_excerpt_more($more) {    global $post;	return '...<a class="link-continue" href="'. get_permalink($post->ID) . '">Read More</a>';}add_filter('excerpt_more', 'eng_excerpt_more');/* Clickable Links */add_filter('the_content', 'make_clickable');?>