<?phpinclude(locate_template('vars.php'));/* New Excerpt */function custom_excerpt_length( $length ) {	if ( is_front_page() ) :	return 10;	else :	return 20;	endif;}add_filter( 'excerpt_length', 'custom_excerpt_length', 999 );function new_excerpt_more($more) {    global $post;	return '...<a class="continuelink button" href="'. get_permalink($post->ID) . '">Read More</a>';}add_filter('excerpt_more', 'new_excerpt_more');/* Clickable Links */add_filter('the_content', 'make_clickable');?>